#!/bin/bash  URL="http://127.0.0.1:$1/api/operation/internal-command/send"ServiceType=$2CmdName=$3CmdMessage=$4RETRY_CNT=5n=0until [ "$n" -ge $RETRY_CNT ]do        CMD_RET=$(curl -s -X POST -F "serviceType=${ServiceType}" -F "commandName=${CmdName}" -F "messageText=${CmdMessage}" "$URL")        if [ $CMD_RET  == "{\"ret_code\":0}" ];then                echo "SUCCESS" && break;        else                echo "RETRY($n)... "        fi        n=$((n+1))        sleep 1doneif [ $n -ge $RETRY_CNT ];then        echo  "FAIL($n) - CODE: $CMD_RET"fi